#ifndef PILEDYNAMIQUE#define PILEDYNAMIQUE#include <stdbool.h>#include "elt_pile.h"/* ----------------------------------        Déclaration des types   --------------------------------*//* Déclaration du type d'une pile (avec masquage) */
typedef struct cell * T_pile; //le type struct cell est defini dans pile.c
/* ----------------------------------            Constructeurs   --------------------------------*//* Initialisation d'une pile  */void initialiser_pile(T_pile *p);/* Empiler */void empiler(T_pile *p, T_element el);/* Depiler */void depiler(T_pile *p);/* ----------------------------------            Accesseurs   --------------------------------*//*sommet de la pile   */T_element sommet_pile(T_pile p);/* affichage d'une pile */void afficher_pile(T_pile p);void afficher_pile_rec(T_pile p);/* la pile est-elle vide ? */bool pile_vide(T_pile p);/*La pile est-elle pleine ? */bool pile_pleine(T_pile p);#endif